const formatBgUrl = ({ bgUrl }, isDay) => {
    const timeOfDay = isDay ? "day" : "night";
    const screenWidth = window.innerWidth < 768 ? "sm" : "lg";
    return BASE_URL + bgUrl[timeOfDay][screenWidth];
};

export default function getAssets({ condition, is_day }) {
    const apiCode = condition.code;
    let weatherAssets;

    switch (apiCode) {
        case 1000:
            weatherAssets = weatherCode.clear;
            break;

        case 1003:
            weatherAssets = weatherCode.partlyCloudy;
            break;

        case 1006:
        case 1009:
            weatherAssets = weatherCode.cloudy;
            break;

        case 1030:
        case 1135:
        case 1147:
            weatherAssets = weatherCode.fog;
            break;

        case 1063:
        case 1150:
        case 1153:
        case 1168:
        case 1180:
        case 1183:
        case 1189:
        case 1198:
        case 1240:
            weatherAssets = weatherCode.lightRain;
            break;

        case 1171:
        case 1186:
        case 1192:
        case 1195:
        case 1201:
        case 1243:
        case 1246:
            weatherAssets = weatherCode.rain;
            break;

        case 1087:
        case 1273:
        case 1276:
        case 1279:
        case 1282:
            weatherAssets = weatherCode.thunder;
            break;

        case 1066:
        case 1114:
        case 1117:
        case 1204:
        case 1207:
        case 1210:
        case 1213:
        case 1216:
        case 1219:
        case 1222:
        case 1237:
        case 1255:
        case 1258:
        case 1261:
        case 1264:
            weatherAssets = weatherCode.snow;
            break;

        default:
            weatherAssets = weatherCode.clear;
    }

    const assets = {};
    assets.text = is_day ? weatherAssets.text.day : weatherAssets.text.night;
    assets.icon = is_day ? weatherAssets.icon.day : weatherAssets.icon.night;
    assets.bgUrl = formatBgUrl(weatherAssets, is_day);

    if (assets.text === "replace") assets.text = condition.text;

    return assets;
}
